  // const [screen, setScreen] = useState(".screen");
  gsap.registerPlugin(ScrollTrigger);

  useEffect(() => {
    var animateIn = gsap.timeline({
      scrollTrigger: {
        trigger: ".codingAnimation",
        start: "top 130px",
        end: "+=1250",
        toggleActions: "play none none none"
      }
    });

    animateIn.to(
      ".fauxHeader",
      {
        duration: 1,
        marginTop: 0,
        ease: "bounce.out"
      },
      "-=1"
    );
  }, []);

